/**
 * File: StatusBar.js
 * Mô tả: Component hiển thị thanh trạng thái tổng quan các kệ hàng trong dashboard quản lý kho.
 * Chức năng: 
 * - Hiển thị số lượng kệ theo từng trạng thái (HIGH, MEDIUM, EMPTY)
 * - Tính toán và hiển thị phần trăm cho mỗi trạng thái
 * - Hiển thị thời gian cập nhật gần nhất
 * - Hỗ trợ accessibility với ARIA labels
 */
import React, { useMemo } from 'react';
import './StatusBar.css';

const StatusBar = ({ stats = {}, lastUpdated }) => {
  // Đảm bảo stats luôn có đủ các trường
  const safeStats = useMemo(() => ({
    high: typeof stats.high === 'number' ? stats.high : 0,
    medium: typeof stats.medium === 'number' ? stats.medium : 0,
    empty: typeof stats.empty === 'number' ? stats.empty : 0,
  }), [stats]);

  // Tính tổng số ô
  const totalTrays = useMemo(() => 
    safeStats.high + safeStats.medium + safeStats.empty, 
    [safeStats]
  );

  // Định dạng phần trăm
  const formatPercentage = (value, total) => {
    if (!total) return '0%';
    return `${((value / total) * 100).toFixed(1)}%`;
  };

  // Định dạng ngày giờ cập nhật
  const formatDate = (date) => {
    if (!date) return '---';
    try {
      const d = typeof date === 'string' ? new Date(date) : date;
      if (isNaN(d.getTime())) return '---';
      
      return new Intl.DateTimeFormat('vi-VN', {
        year: 'numeric',
        month: 'numeric',
        day: 'numeric',
        hour: 'numeric',
        minute: 'numeric',
        second: 'numeric',
        hour12: false
      }).format(d);
    } catch (error) {
      console.error('Lỗi khi định dạng ngày:', error);
      return '---';
    }
  };

  // Định nghĩa các mô tả và màu sắc cho từng trạng thái
  const statusConfig = {
    high: {
      label: 'HIGH',
      description: 'Ô chứa nhiều',
      dotClass: 'high-dot'
    },
    medium: {
      label: 'MEDIUM',
      description: 'Ô chứa vừa phải',
      dotClass: 'medium-dot'
    },
    empty: {
      label: 'EMPTY',
      description: 'Ô trống',
      dotClass: 'empty-dot'
    }
  };

  return (
    <div className="status-bar" role="status" aria-label="Thống kê trạng thái kệ hàng">
      <div className="status-bar-items">
        {Object.keys(statusConfig).map(statusKey => (
          <div 
            key={statusKey}
            className="stat-item" 
            title={`Số ${statusConfig[statusKey].description.toLowerCase()}`}
          >
            <div className="stat-content">
              <span 
                className={`dot ${statusConfig[statusKey].dotClass}`} 
                aria-label={statusConfig[statusKey].description}
              ></span>
              <span className="stat-label">{statusConfig[statusKey].label}:</span>
              <span className="stat-value">{safeStats[statusKey]}</span>
            </div>
            <div className="stat-percentage">
              {formatPercentage(safeStats[statusKey], totalTrays)}
            </div>
          </div>
        ))}
      </div>

      <div className="last-updated" title="Thời gian cập nhật thống kê">
        Cập nhật: {formatDate(lastUpdated)}
      </div>
    </div>
  );
};

export default StatusBar;
