/**
 * File: FilterPanel.js
 * Mô tả: Component hiển thị và xử lý bộ lọc cho dashboard quản lý kho hàng.
 * Chức năng: 
 * - Hiển thị giao diện cho việc lọc theo tầng và trạng thái kệ hàng
 * - Cho phép chọn/bỏ chọn các bộ lọc
 * - Hiển thị các bộ lọc đang hoạt động
 * - Cung cấp tính năng đặt lại bộ lọc
 * - Tự động cập nhật giao diện khi thay đổi bộ lọc
 * - Hỗ trợ accessibility với ARIA attributes
 */
import React, { useMemo } from 'react';
import './FilterPanel.css';

const FilterPanel = ({
  onFilterChange,
  tiers = [],
  statuses = ['HIGH', 'MEDIUM', 'EMPTY'],
  filters,
  shelfStats
}) => {
  // Mapping trạng thái với mô tả và màu sắc
  const statusDescriptions = useMemo(() => ({
    HIGH: { label: 'Kệ đầy', color: '#22c55e' },
    MEDIUM: { label: 'Kệ còn trống một phần', color: '#f59e0b' },
    EMPTY: { label: 'Kệ trống hoàn toàn', color: '#ef4444' },
  }), []);

  // Sắp xếp danh sách tầng
  const sortedTiers = useMemo(() => {
    return [...new Set(tiers)].sort((a, b) => Number(a) - Number(b));
  }, [tiers]);

  // Xử lý thay đổi bộ lọc
  const handleFilterChange = (filterType, value) => {
    if (!onFilterChange) return;

    const newFilters = {
      ...filters,
      [filterType]: value,
    };
    onFilterChange(filterType, value, newFilters);
  };

  // Reset tất cả bộ lọc
  const resetFilters = () => {
    if (!onFilterChange) return;

    const resetState = {
      tier: 'all',
      status: 'all',
    };
    onFilterChange('reset', null, resetState);
  };

  // Tính tổng số lượng cho mỗi trạng thái
  const totalItems = useMemo(() => {
    return Object.values(shelfStats || {}).reduce((acc, curr) => acc + (curr || 0), 0);
  }, [shelfStats]);

  // Kiểm tra xem bộ lọc có đang áp dụng không
  const hasActiveFilters = filters?.tier !== 'all' || filters?.status !== 'all';

  // Tính phần trăm mỗi trạng thái
  const getPercentage = (status) => {
    const count = shelfStats?.[status.toLowerCase()] || 0;
    if (!totalItems) return 0;
    return Math.round((count / totalItems) * 100);
  };

  return (
    <div className="filter-panel" role="region" aria-label="Bộ lọc">
      <div className="filter-header">
        <h3>Bộ lọc</h3>
        <button
          className="reset-button"
          onClick={resetFilters}
          disabled={!hasActiveFilters}
          title="Đặt lại tất cả bộ lọc"
          aria-label="Đặt lại tất cả bộ lọc"
        >
          Đặt lại
        </button>
      </div>

      <div className="filter-group">
        <label htmlFor="tier-select">Chọn tầng:</label>
        <select
          id="tier-select"
          value={filters?.tier || 'all'}
          onChange={(e) => handleFilterChange('tier', e.target.value)}
          aria-label="Chọn tầng để lọc"
        >
          <option value="all">Tất cả các tầng</option>
          {sortedTiers.map((tier) => (
            <option key={tier} value={tier.toString()}>
              Tầng {tier}
            </option>
          ))}
        </select>
      </div>

      <div className="filter-group">
        <label htmlFor="status-select">Trạng thái kệ:</label>
        <select
          id="status-select"
          value={filters?.status || 'all'}
          onChange={(e) => handleFilterChange('status', e.target.value)}
          style={{
            borderLeft: filters?.status !== 'all' ? `4px solid ${statusDescriptions[filters.status]?.color || 'transparent'}` : 'none'
          }}
          aria-label="Chọn trạng thái kệ để lọc"
        >
          <option value="all">Tất cả trạng thái</option>
          {statuses.map((status) => {
            const count = shelfStats?.[status.toLowerCase()] || 0;
            const percentage = getPercentage(status);
            
            return (
              <option 
                key={status} 
                value={status}
                className={`status-option status-${status.toLowerCase()}`}
              >
                {statusDescriptions[status]?.label || status}
                {` (${count} - ${percentage}%)`}
              </option>
            );
          })}
        </select>
      </div>

      {/* Hiển thị các bộ lọc đang áp dụng */}
      <div className="active-filters" aria-live="polite">
        {hasActiveFilters && (
          <div className="filter-tags">
            <span>Đang lọc theo: </span>
            {filters.tier !== 'all' && (
              <span className="filter-tag" role="status">
                Tầng {filters.tier}
                <button 
                  onClick={() => handleFilterChange('tier', 'all')} 
                  aria-label="Xóa bộ lọc tầng"
                  title="Xóa bộ lọc tầng"
                >×</button>
              </span>
            )}
            {filters.status !== 'all' && (
              <span 
                className="filter-tag"
                style={{ 
                  borderLeft: `4px solid ${statusDescriptions[filters.status]?.color || 'transparent'}`,
                  paddingLeft: '8px'
                }}
                role="status"
              >
                {statusDescriptions[filters.status]?.label || filters.status}
                <button 
                  onClick={() => handleFilterChange('status', 'all')} 
                  aria-label="Xóa bộ lọc trạng thái"
                  title="Xóa bộ lọc trạng thái"
                >×</button>
              </span>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default FilterPanel;
