/**
 * File: ProtectedRoute.js
 * Mô tả: Component bảo vệ các route yêu cầu xác thực trong ứng dụng quản lý kho hàng.
 * Chức năng: 
 * - Kiểm tra trạng thái đăng nhập của người dùng
 * - Cho phép truy cập vào route nếu đã đăng nhập
 * - Chuyển hướng về trang đăng nhập nếu chưa đăng nhập
 * - Hiển thị trạng thái đang tải trong quá trình kiểm tra xác thực
 */
import React from 'react';
import { Route, Redirect } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import './ProtectedRoute.css';

const ProtectedRoute = ({ component: Component, ...rest }) => {
    const { user, loading } = useAuth();

    // Hiển thị trạng thái đang tải khi đang kiểm tra xác thực
    if (loading) {
        return (
            <div className="loading-container" aria-live="polite" role="status">
                <div className="loading-spinner"></div>
                <p>Đang tải...</p>
            </div>
        );
    }

    // Kiểm tra xác thực và điều hướng
    return (
        <Route
            {...rest}
            render={props => 
                user ? (
                    // Đã xác thực: hiển thị component được yêu cầu
                    <Component {...props} user={user} />
                ) : (
                    // Chưa xác thực: chuyển hướng đến trang đăng nhập
                    <Redirect
                        to={{
                            pathname: "/login",
                            state: { from: props.location }
                        }}
                    />
                )
            }
        />
    );
};

export default ProtectedRoute;
