// File: FilterPanel.js
// Mô tả: Component React hiển thị và xử lý bộ lọc (filter) cho dashboard quản lý kho hàng.
// Chức năng: Cho phép người dùng lọc dữ liệu kệ hàng theo tầng và trạng thái, đồng bộ với dashboard.
import React from 'react';
import './FilterPanel.css';


const FilterPanel = ({
  onFilterChange,
  tiers = [],
  statuses = ['HIGH', 'MEDIUM', 'EMPTY'],
  filters,
  shelfStats
}) => {
  // Mapping trạng thái với mô tả
  const statusDescriptions = {
    HIGH: 'Kệ đầy',
    MEDIUM: 'Kệ còn trống một phần',
    EMPTY: 'Kệ trống hoàn toàn',
  };

  // Xử lý thay đổi bộ lọc
  const handleFilterChange = (filterType, value) => {
    const newFilters = {
      ...filters,
      [filterType]: value,
    };
    onFilterChange(filterType, value, newFilters);
  };

  // Reset tất cả bộ lọc
  const resetFilters = () => {
    const resetState = {
      tier: 'all',
      status: 'all',
    };
    onFilterChange('reset', null, resetState);
  };

  return (
    <div className="filter-panel">
      <div className="filter-header">
        <h3>Bộ lọc</h3>
        <button
          className="reset-button"
          onClick={resetFilters}
          disabled={filters.tier === 'all' && filters.status === 'all'}
        >
          Đặt lại
        </button>
      </div>

      <div className="filter-group">
        <label htmlFor="tier-select">Chọn tầng:</label>
        <select
          id="tier-select"
          value={filters.tier}
          onChange={(e) => handleFilterChange('tier', e.target.value)}
        >
          <option value="all">Tất cả các tầng</option>
          {tiers.map((tier) => (
            <option key={tier} value={tier.toString()}>
              Tầng {tier}
            </option>
          ))}
        </select>
      </div>

      <div className="filter-group">
        <label htmlFor="status-select">Trạng thái kệ:</label>
        <select
          id="status-select"
          value={filters.status}
          onChange={(e) => handleFilterChange('status', e.target.value)}
        >
          <option value="all">Tất cả trạng thái</option>
          {statuses.map((status) => (
            <option key={status} value={status}>
              {statusDescriptions[status]}
              {shelfStats && typeof shelfStats[status.toLowerCase()] === 'number' 
                ? ` (${shelfStats[status.toLowerCase()]})`
                : ''}
            </option>
          ))}
        </select>
      </div>

      {/* Hiển thị các bộ lọc đang áp dụng */}
      <div className="active-filters">
        {(filters.tier !== 'all' || filters.status !== 'all') && (
          <div className="filter-tags">
            <span>Đang lọc theo: </span>
            {filters.tier !== 'all' && (
              <span className="filter-tag">
                Tầng {filters.tier}
                <button onClick={() => handleFilterChange('tier', 'all')} aria-label="Xóa bộ lọc tầng">×</button>
              </span>
            )}
            {filters.status !== 'all' && (
              <span className="filter-tag">
                {statusDescriptions[filters.status]}
                <button onClick={() => handleFilterChange('status', 'all')} aria-label="Xóa bộ lọc trạng thái">×</button>
              </span>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default FilterPanel;
