// File: Dashboard.js
// Mô tả: Component React chính hiển thị dashboard quản lý kho hàng.
// Chức năng: Quản lý trạng thái, lọc, thống kê, hiển thị lưới kệ, chi tiết kệ, và đồng bộ dữ liệu mock cho toàn bộ hệ thống.
import React, { useState, useEffect, useRef } from 'react';
import { useHistory } from 'react-router-dom';
import '../styles/variables.css';
import '../styles/global.css';
import '../styles/responsive.css';
import './Dashboard.css';
import ShelfGrid from '../components/ShelfGrid/ShelfGrid';
import FilterPanel from '../components/FilterPanel/FilterPanel';
import ShelfDetail from '../components/ShelfDetail/ShelfDetail';
import { shelf } from '../utils/api';

const Dashboard = ({ user, onLogout }) => {
  const history = useHistory();
  const isMounted = useRef(true);
  const [shelves, setShelves] = useState([]);
  const [filteredShelves, setFilteredShelves] = useState([]);
  const [selectedShelf, setSelectedShelf] = useState(null);
  const [filters, setFilters] = useState({ tier: 'all', status: 'all' });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const handleLogout = () => {
    try {
      onLogout();
      history.push('/login');
    } catch (error) {
      console.error('Lỗi khi đăng xuất:', error);
    }
  };

  // Loại bỏ hàm calculateStats vì không cần thiết nữa

  const getShelfStats = () => {
    const stats = { high: 0, medium: 0, empty: 0 };

    shelves.forEach(shelf => {
      if (shelf.tiers) {
        shelf.tiers.forEach(tier => {
          if (tier.trays) {
            tier.trays.forEach(tray => {
              const status = tray.status ? tray.status.toLowerCase() : 'empty';
              if (stats[status] !== undefined) {
                stats[status]++;
              }
            });
          }
        });
      }
    });

    return stats;
  };

  const getAllTiers = () => {
    const tiers = new Set();
    shelves.forEach(shelf => {
      shelf.tiers.forEach(tier => {
        tiers.add(tier.tier);
      });
    });
    return Array.from(tiers);
  };

  // Áp dụng bộ lọc
  const applyFilters = (shelfList, currentFilters) => {
    if (!Array.isArray(shelfList)) return [];

    return shelfList.map(shelf => ({
      ...shelf,
      tiers: shelf.tiers.map(tier => ({
        ...tier,
        trays: tier.trays.map(tray => ({
          ...tray,
          isVisible: (currentFilters.tier === 'all' || tier.tier === parseInt(currentFilters.tier)) &&
                    (currentFilters.status === 'all' || tray.status === currentFilters.status)
        }))
      }))
    })).filter(shelf => shelf.tiers.some(tier => tier.trays.some(tray => tray.isVisible)));
  };

  // Load dữ liệu ban đầu
  // Cleanup khi component unmount
  useEffect(() => {
    return () => {
      isMounted.current = false;
    };
  }, []);

  // Load dữ liệu ban đầu
  useEffect(() => {
    const loadData = async () => {
      try {
        setLoading(true);
        const data = await shelf.fetchAll();
        if (!isMounted.current) return;
        setShelves(data);
        const filtered = applyFilters(data, filters);
        setFilteredShelves(filtered);
      } catch (err) {
        if (isMounted.current) setError(err.message);
      } finally {
        if (isMounted.current) setLoading(false);
      }
    };
    loadData();
  }, [filters]);


  // Subscribe to shelf updates (mock)
  useEffect(() => {
    let unsub = null;
    const subscribe = async () => {
      unsub = await shelf.subscribeToUpdates((update) => {
        if (isMounted.current) {
          setShelves(prevShelves => {
            const newShelves = prevShelves.map(s =>
              s.tier === update.tier && s.tray === update.tray
                ? { ...s, ...update }
                : s
            );
            const filtered = applyFilters(newShelves, filters);
            setFilteredShelves(filtered);
            return newShelves;
          });
        }
      });
    };
    subscribe();
    return () => { 
      if (unsub) unsub();
      isMounted.current = false;
    };
  }, [filters]);


  // Xử lý thay đổi bộ lọc
  const handleFilterChange = (type, value, newFilters) => {
    console.log(`Filter changed: ${type} = ${value}`);
    const nextFilters = newFilters || { ...filters, [type]: value };
    setFilters(nextFilters);

    // Tính toán lại danh sách đã lọc
    const filtered = applyFilters(shelves, nextFilters);
    setFilteredShelves(filtered);

    // Nếu đang chọn một shelf nhưng nó không còn trong kết quả lọc, bỏ chọn
    if (selectedShelf) {
      const isStillVisible = filtered.some(shelf => 
        shelf.id === selectedShelf.id || 
        shelf._id === selectedShelf._id
      );

      if (!isStillVisible) {
        setSelectedShelf(null);
      }
    }
  };


  if (loading) {
    return <div className="loading" role="status">Đang tải dữ liệu...</div>;
  }

  if (error) {
    return <div className="error" role="alert">Lỗi: {error}</div>;
  }

  return (
    <div className="dashboard">
      <div className="dashboard-header">
        <h1 className="dashboard-title">Warehouse Dashboard</h1>
        <div className="user-info">
          <span className="user-name">{user?.username || 'Admin'}</span>
          <button className="logout-button" onClick={handleLogout}>
            Đăng xuất
          </button>
        </div>
      </div>

      <FilterPanel 
        tiers={getAllTiers()}
        statuses={['HIGH', 'MEDIUM', 'EMPTY']}
        filters={filters}
        onFilterChange={handleFilterChange}
        shelfStats={getShelfStats()}
      />

      <ShelfGrid 
        shelves={filteredShelves}
        onShelfClick={setSelectedShelf}
      />

      {selectedShelf && (
        <ShelfDetail 
          shelf={selectedShelf}
          onClose={() => setSelectedShelf(null)}
        />
      )}
    </div>
  );
};

export default Dashboard;
