import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { loginUser } from '../utils/auth';
import './Login.css';

const Login = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false);
    const [showPassword, setShowPassword] = useState(false);
    const navigate = useNavigate();
    const { login } = useAuth();

    // Clear error when inputs change
    useEffect(() => {
        if (error) setError('');
    }, [username, password]);

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');
        setLoading(true);
        
        try {
            const response = await loginUser(username, password);
            if (response.success) {
                login(response.user, response.token);
                navigate('/dashboard');
            }
        } catch (err) {
            setError(err.message || 'ƒêƒÉng nh·∫≠p th·∫•t b·∫°i. Vui l√≤ng th·ª≠ l·∫°i.');
            console.error('L·ªói ƒëƒÉng nh·∫≠p:', err);
        } finally {
            setLoading(false);
        }
    };

    const handleSampleLogin = (sampleUsername, samplePassword) => {
        setUsername(sampleUsername);
        setPassword(samplePassword);
    };

    return (
        <div className="login-container">
            <div className="login-box">
                <h2>Shelf Dashboard</h2>
                <form onSubmit={handleSubmit}>
                    <div className="form-group">
                        <label htmlFor="username">T√™n ƒëƒÉng nh·∫≠p</label>
                        <input
                            type="text"
                            id="username"
                            value={username}
                            onChange={(e) => setUsername(e.target.value)}
                            required
                            disabled={loading}
                            placeholder="Nh·∫≠p t√™n ƒëƒÉng nh·∫≠p"
                            autoComplete="username"
                            autoFocus
                        />
                    </div>
                    <div className="form-group">
                        <label htmlFor="password">M·∫≠t kh·∫©u</label>
                        <div className="password-input">
                            <input
                                type={showPassword ? "text" : "password"}
                                id="password"
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                                required
                                disabled={loading}
                                placeholder="Nh·∫≠p m·∫≠t kh·∫©u"
                                autoComplete="current-password"
                            />
                            <button
                                type="button"
                                className="show-password-btn"
                                onClick={() => setShowPassword(!showPassword)}
                                tabIndex="-1"
                                aria-label={showPassword ? "·∫®n m·∫≠t kh·∫©u" : "Hi·ªán m·∫≠t kh·∫©u"}
                            >
                                {showPassword ? 'üëÅÔ∏è' : 'üëÅÔ∏è‚Äçüó®Ô∏è'}
                            </button>
                        </div>
                    </div>
                    {error && (
                        <div className="error-message" role="alert">
                            {error}
                        </div>
                    )}
                    <button 
                        type="submit" 
                        className="login-button" 
                        disabled={loading}
                    >
                        {loading ? 'ƒêang x·ª≠ l√Ω...' : 'ƒêƒÉng Nh·∫≠p'}
                    </button>
                </form>
                {/* <div className="login-help">
                    <p>T√†i kho·∫£n th·ª≠ nghi·ªám</p>
                    <ul>
                        <li 
                            onClick={() => handleSampleLogin('admin', 'admin123')}
                            title="Click ƒë·ªÉ ƒëi·ªÅn th√¥ng tin ƒëƒÉng nh·∫≠p"
                        >
                            Admin: admin / admin123
                        </li>
                        <li 
                            onClick={() => handleSampleLogin('user', 'user123')}
                            title="Click ƒë·ªÉ ƒëi·ªÅn th√¥ng tin ƒëƒÉng nh·∫≠p"
                        >
                            User: user / user123
                        </li>
                    </ul>
                </div> */}
            </div>
        </div>
    );
};

export default Login;
