/**
 * File: ShelfGrid.js
 * Mô tả: Component hiển thị lưới các kệ hàng trong dashboard quản lý kho.
 * Chức năng: 
 * - Hiển thị danh sách kệ hàng theo tầng và khay
 * - Áp dụng các bộ lọc theo tầng và trạng thái kệ
 * - Hiển thị trạng thái kệ bằng màu sắc và hiệu ứng trực quan
 * - Cho phép tương tác để xem chi tiết mỗi kệ
 * - Tự động cập nhật hiển thị khi dữ liệu thay đổi
 * - Cung cấp thống kê tổng hợp về trạng thái kệ
 * - Hỗ trợ accessibility với data attributes và ARIA
 */
import React, { useState, useEffect, useCallback, useMemo } from 'react';
import './ShelfGrid.css';
import ShelfItem from './ShelfItem';
import ShelfDetail from '../ShelfDetail/ShelfDetail';
import StatusBar from '../StatusBar/StatusBar';
import FilterPanel from '../FilterPanel/FilterPanel';

const ShelfGrid = ({ shelves, onShelfClick }) => {
  // Các hằng số cấu hình
  const NUM_SHELVES = 4; // Số lượng kệ đồ
  const NUM_TIERS_PER_SHELF = 4; // Số tầng mỗi kệ
  const NUM_TRAYS_PER_TIER = 6; // Số ô mỗi tầng

  // State component
  const [selectedShelf, setSelectedShelf] = useState(null);
  const [stats, setStats] = useState({ high: 0, medium: 0, empty: 0 });
  const [lastUpdated, setLastUpdated] = useState(new Date().toISOString());
  const [shelvesData, setShelvesData] = useState([]);
  const [filters, setFilters] = useState({
    tier: 'all',
    status: 'all'
  });

  // Hàm tính toán trạng thái dựa trên số lượng vật phẩm và sức chứa
  const calculateStatus = useCallback((itemCount, capacity) => {
    const percentage = (itemCount / capacity) * 100;
    if (percentage >= 70) return 'HIGH';
    if (percentage >= 30) return 'MEDIUM';
    return 'EMPTY';
  }, []);

  // Khởi tạo dữ liệu ban đầu và thiết lập interval cập nhật
  useEffect(() => {
    // Tạo dữ liệu mẫu ban đầu
    const initialData = Array.from({ length: NUM_SHELVES }, (_, shelfIndex) => {
      const tiers = Array.from({ length: NUM_TIERS_PER_SHELF / 2 }, (_, groupIndex) => {
        const groupedTiers = [
          { 
            tier: groupIndex * 2 + 1, 
            trays: Array.from({ length: NUM_TRAYS_PER_TIER }, (_, trayIndex) => {
              const capacity = 100;
              const itemCount = Math.floor(Math.random() * (capacity + 1));
              return {
                tray: trayIndex + 1,
                status: calculateStatus(itemCount, capacity),
                capacity,
                itemCount,
                lastUpdated: new Date().toISOString()
              };
            })
          },
          { 
            tier: groupIndex * 2 + 2, 
            trays: Array.from({ length: NUM_TRAYS_PER_TIER }, (_, trayIndex) => {
              const capacity = 100;
              const itemCount = Math.floor(Math.random() * (capacity + 1));
              return {
                tray: trayIndex + 1,
                status: calculateStatus(itemCount, capacity),
                capacity,
                itemCount,
                lastUpdated: new Date().toISOString()
              };
            })
          }
        ];
        return groupedTiers;
      });
      return { shelf: shelfIndex + 1, tiers };
    });

    setShelvesData(initialData);

    // Thiết lập interval để cập nhật ngẫu nhiên
    const interval = setInterval(() => {
      setShelvesData(prevData => {
        const newData = JSON.parse(JSON.stringify(prevData));
        
        // Chọn ngẫu nhiên một ô để cập nhật
        const randomShelf = Math.floor(Math.random() * NUM_SHELVES);
        const randomTierGroup = Math.floor(Math.random() * (NUM_TIERS_PER_SHELF / 2));
        const randomTierInGroup = Math.floor(Math.random() * 2);
        const randomTray = Math.floor(Math.random() * NUM_TRAYS_PER_TIER);
        
        // Cập nhật thông tin của ô được chọn
        const tray = newData[randomShelf].tiers[randomTierGroup][randomTierInGroup].trays[randomTray];
        const newItemCount = Math.floor(Math.random() * (tray.capacity + 1));
        tray.itemCount = newItemCount;
        tray.status = calculateStatus(newItemCount, tray.capacity);
        tray.lastUpdated = new Date().toISOString();
        
        return newData;
      });
      setLastUpdated(new Date().toISOString());
    }, 3000);

    // Cleanup interval khi component unmount
    return () => clearInterval(interval);
  }, [calculateStatus, NUM_SHELVES, NUM_TIERS_PER_SHELF, NUM_TRAYS_PER_TIER]);

  // Tính toán thống kê mỗi khi dữ liệu thay đổi
  useEffect(() => {
    const newStats = { high: 0, medium: 0, empty: 0 };
    
    shelvesData.forEach(shelf => {
      shelf.tiers.forEach(group => {
        group.forEach(tier => {
          tier.trays.forEach(tray => {
            const status = tray.status?.toLowerCase() || 'empty';
            if (newStats[status] !== undefined) {
              newStats[status]++;
            }
          });
        });
      });
    });
    
    setStats(newStats);
  }, [shelvesData]);

  // Xử lý click vào một ô
  const handleTrayClick = useCallback((shelfIndex, tierInfo, trayIndex) => {
    // Kiểm tra để tránh click vào khu vực không hợp lệ
    if (!tierInfo || !tierInfo.trays || !tierInfo.trays[trayIndex]) {
      return;
    }
  
    const tray = tierInfo.trays[trayIndex];
    
    setSelectedShelf({
      shelf: shelfIndex + 1,
      tier: tierInfo.tier,
      tray: tray.tray,
      status: tray.status,
      capacity: tray.capacity,
      itemCount: tray.itemCount,
      lastUpdated: tray.lastUpdated
    });

    // Thông báo ra bên ngoài nếu có callback
    if (onShelfClick) {
      onShelfClick({
        shelf: shelfIndex + 1,
        tier: tierInfo.tier,
        tray: tray.tray
      });
    }
  }, [onShelfClick]);

  // Hàm kiểm tra một ô có phù hợp với bộ lọc hay không
  const isItemMatchingFilters = useCallback((tierNumber, trayStatus) => {
    // Kiểm tra match với tầng đã chọn
    const tierMatch = filters.tier === 'all' || Number(filters.tier) === tierNumber;
    
    // Kiểm tra match với trạng thái đã chọn - đảm bảo so sánh chính xác
    const statusMatch = filters.status === 'all' || filters.status === trayStatus;

    // Nếu có bất kỳ bộ lọc nào được áp dụng, thực hiện kiểm tra chặt chẽ hơn
    const hasActiveFilters = filters.status !== 'all' || filters.tier !== 'all';
    
    // Chỉ trả về kết quả khớp nếu cả hai điều kiện đều đúng
    return tierMatch && statusMatch;
  }, [filters]);

  // Hàm kiểm tra một hàng (tier) có bất kỳ ô nào phù hợp với bộ lọc không
  const hasTierMatchingItems = useCallback((tier) => {
    // Nếu không có bộ lọc đang hoạt động, mọi tier đều phù hợp
    if (filters.tier === 'all' && filters.status === 'all') {
      return true;
    }
    
    const hasMatching = tier.trays.some(tray => {
      return isItemMatchingFilters(tier.tier, tray.status);
    });
    
    return hasMatching;
  }, [filters, isItemMatchingFilters]);

  // Đóng chi tiết kệ
  const handleCloseDetail = useCallback(() => {
    setSelectedShelf(null);
  }, []);

  // Render component
  return (
    <div className="shelf-grid-wrapper" role="region" aria-label="Lưới kệ hàng">
      <StatusBar stats={stats} lastUpdated={lastUpdated} />
      <div className="shelf-grid-container">
        {shelvesData.map((shelf, shelfIndex) => (
          <div 
            key={`shelf-${shelf.shelf}`} 
            className="shelf-grid"
            data-shelf-id={shelf.shelf}
          >
            <h3 id={`shelf-${shelf.shelf}-heading`}>Kệ {shelf.shelf}</h3>
            {shelf.tiers.map((group, groupIndex) => (
              <div 
                key={`tier-group-${groupIndex}`} 
                className="tier-row-group"
                data-shelf={shelf.shelf}
                data-group-index={groupIndex}
              >
                {group.map((tier) => {
                  const hasMatchingItems = hasTierMatchingItems(tier);
                  // Chỉ áp dụng các class đặc biệt khi có bộ lọc đang được kích hoạt
                  const hasActiveFilters = filters.status !== 'all' || filters.tier !== 'all';
                  const tierFilterClass = hasActiveFilters
                    ? (hasMatchingItems ? 'has-matching-items' : 'no-matching-items')
                    : '';
                    
                  return (
                    <div 
                      key={`tier-${tier.tier}`} 
                      className={`tier-row ${tierFilterClass}`}
                      style={{
                        transition: `all 0.7s cubic-bezier(0.4, 0, 0.2, 1) ${tier.tier * 0.1}s`
                      }}
                      data-tier={tier.tier}
                      data-has-matching={hasMatchingItems}
                      data-active-filters={hasActiveFilters}
                      aria-labelledby={`shelf-${shelf.shelf}-tier-${tier.tier}-heading`}
                    >
                      <div 
                        id={`shelf-${shelf.shelf}-tier-${tier.tier}-heading`}
                        className="tier-label"
                      >
                        Tầng {tier.tier}
                      </div>
                      <div 
                        className="tray-container"
                        data-tier={tier.tier}
                        role="group"
                        aria-label={`Tầng ${tier.tier}`}
                      >
                        {tier.trays.map((tray, trayIndex) => {
                          const isMatching = isItemMatchingFilters(tier.tier, tray.status);
                          // Tạo class đặc biệt khi có bộ lọc đang được áp dụng
                          const hasActiveFilters = filters.status !== 'all' || filters.tier !== 'all';
                          const filterClass = hasActiveFilters 
                            ? (isMatching ? 'matching-filter' : 'not-matching-filter')
                            : '';
                          
                          return (
                            <div 
                              key={`tray-${tier.tier}-${tray.tray}`} 
                              className={`tray-cell ${filterClass}`}
                              style={{
                                transition: `all 0.5s cubic-bezier(0.4, 0, 0.2, 1) ${trayIndex * 0.05}s`
                              }}
                              data-tier={tier.tier}
                              data-tray={tray.tray}
                              data-status={tray.status}
                              data-filter-match={isMatching}
                              data-active-filters={hasActiveFilters}
                            >
                              <ShelfItem
                                tier={tier.tier}
                                tray={tray.tray}
                                status={tray.status}
                                onClick={() => handleTrayClick(shelfIndex, tier, trayIndex)}
                                isFiltered={hasActiveFilters && isMatching}
                              />
                            </div>
                          );
                        })}
                      </div>
                    </div>
                  );
                })}
              </div>
            ))}
          </div>
        ))}
      </div>
      {selectedShelf && (
        <ShelfDetail
          shelf={selectedShelf}
          onClose={handleCloseDetail}
        />
      )}
    </div>
  );
};

export default ShelfGrid;
